find . -type f \( -name "*.png" -o -name "*.jpg" -o -name "*.jpeg" \) -exec mogrify -filter Triangle -define filter:support=2 -unsharp 0.25x0.08+8.3+0.045 -dither None -posterize 136 -quality 82 -define png:compression-filter=5 -define png:compression-level=9 -define png:compression-strategy=1 -define png:exclude-chunk=all -interlace none -colorspace sRGB {} \; -o -type f -name "*.gif" -exec gifsicle --batch -O3 --unoptimize --colors 256 -o {} \;
